package com.mobile.younthcanteen.bean;import android.text.TextUtils;import java.util.Map;public class PayResult {	private String resultStatus;	private String result;	private String memo;	public PayResult(Map<String, String> rawResult) {		if (rawResult == null) {			return;		}		for (String key : rawResult.keySet()) {			if (TextUtils.equals(key, "resultStatus")) {				resultStatus = rawResult.get(key);			} else if (TextUtils.equals(key, "result")) {				result = rawResult.get(key);			} else if (TextUtils.equals(key, "memo")) {				memo = rawResult.get(key);			}		}	}	@Override	public String toString() {		if (TextUtils.isEmpty(resultStatus)) {			resultStatus = "\"\"";		}		if (TextUtils.isEmpty(result)) {			result = "\"\"";		}		if (TextUtils.isEmpty(memo)) {			memo = "\"\"";		}		return "{\"resultStatus\":" + resultStatus + ",\"result\":" + result				+ ",\"memo\":" + memo + "}";	}	/**	 * @return the resultStatus	 */	public String getResultStatus() {		return resultStatus;	}	/**	 * @return the memo	 */	public String getMemo() {		return memo;	}	/**	 * @return the result	 */	public String getResult() {		return result;	}}